// FIXME: Add standard header.

#ifndef SBBM_INSTPRINTER_H
#define SBBM_INSTPRINTER_H

#include "llvm/MC/MCInstPrinter.h"

namespace llvm {

class TargetMachine;

class SbbmInstPrinter : public MCInstPrinter {
public:
  SbbmInstPrinter(
    const MCAsmInfo &MAI, const MCInstrInfo &MII, const MCRegisterInfo &MRI)
    : MCInstPrinter(MAI, MII, MRI)
  { }

  // printInstruction and getRegisterName are generated by tblgen.
  void printInstruction(const MCInst *MI, raw_ostream &O);
  static const char *getRegisterName(unsigned RegNo);

  virtual void printInst(const MCInst *MI, raw_ostream &O, StringRef Annot)
    override;

  virtual void printRegName(raw_ostream &OS, unsigned RegNo) const override;

private:
  void printOperand(const MCInst *MI, unsigned OpNo, raw_ostream &O);
  void printPredicate(const MCInst *MI, raw_ostream &O);
  void printAddrModeMemSrc(const MCInst *MI, unsigned OpNum, raw_ostream &O);
};

} // namespace llvm

#endif // include guard
